import React, { useState } from "react";

function Note({ id, title, content, dispatch }) {
  const handleClick = () => {
    dispatch({ type: "DELETE", payload: id });
  };
  return (
    <div className="note">
      <h1>{title}</h1>
      <p>{content}</p>
      <button onClick={handleClick}>DELETE</button>
      <button onClick={handleClick}>EDIT</button>
    </div>
  );
}

export default Note;
