import React, { useState } from "react";

function Note({ id, title, content, dispatch }) {
  return (
    <div className="note">
      <h1>{title}</h1>
      <p>{content}</p>
      <button
        onClick={() => {
          dispatch({ type: "DELETE", payload: id });
        }}
      >
        DELETE
      </button>
    </div>
  );
}

export default Note;
