import React, { useReducer, useState } from "react";
import Header from "./Header";
import Footer from "./Footer";
import Note from "./Note";
import CreateArea from "./CreateArea";

function App() {
  // const [notesArray, setNotesArray] = useState([]);
  const [notesArray, dispatch] = useReducer(notesArrayReducer, []);
  const notesArrayReducer = (notesArray, action) => {
    switch (action.type) {
      case "ADD":
        return [...notesArray, action.payload];
    }
  };

  function addItem(newItem) {
    setNotesArray((prevValue) => {
      return [...prevValue, newItem];
    });
  }

  function deleteItem(id) {
    setNotesArray((prevValue) => {
      return prevValue.filter((value, index) => {
        return index !== id;
      });
    });
  }
  return (
    <div>
      <Header />
      <CreateArea addItem={addItem} />
      {notesArray.map((note, index) => {
        return (
          <Note
            key={index}
            id={index}
            title={note.title}
            content={note.content}
            deleteItem={deleteItem}
          />
        );
      })}
      <Footer />
    </div>
  );
}

export default App;
